<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use Carbon\Carbon;class Post extends Model{    protected $fillable = ['title','date_end', 'date_start'];    public function comments()    {        return $this->hasMany('App\Comment');    }    public function tags(){        return $this->belongsToMany('App\Tag');    }    public function getDateStartAttribute($value)    {        return Carbon::parse($value)->format('d-m-Y H:i:s');    }    public function getDateEndAttribute($value)    {        return Carbon::parse($value)->format('d-m-Y H:i:s');    }    public function scopePublished($query, $id=null){        if(is_null($id))            return $query->where('status','=','publish');        return $query->whereRaw('status=? AND id=?', ['publish', (int)$id]);    }}