<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use Carbon\Carbon;class Post extends Model{    protected $fillable = [ 'title',                            'content',                            'date_start',                            'date_end',                            'status',                            'slug',                            'excerpt',                            'thumbnail_link',                            'url_site',                            'user_id',                        ];    public function comments()    {        return $this->hasMany('App\Comment');    }    public function tags(){        return $this->belongsToMany('App\Tag');    }    public function getDateStartAttribute($value)    {        return Carbon::parse($value)->format('d-m-Y H:i:s');    }    public function getDateEndAttribute($value)    {        return Carbon::parse($value)->format('d-m-Y H:i:s');    }    public function setDateStartAttribute($value)    {        $this->attributes['date_start'] = Carbon::createFromFormat('d-m-Y H:i:s',$value)->toDateTimeString();    }    public function setDateEndAttribute($value)    {        $this->attributes['date_end']= Carbon::createFromFormat('d-m-Y H:i:s',$value)->toDateTimeString();    }    public function scopePublished($query, $id=null){        if(is_null($id))            return $query->where('status','=','publish');        return $query->whereRaw('status=? AND id=?', ['publish', (int)$id]);    }}